const { MessageEmbed } = require("discord.js");

module.exports = {
    name: "loop",
    aliases: ['l'],
    cooldown: 5,
    description: `Repeat the song you played!.`,
    run: async(client, message, args) => {
        if (client.loop_command == true) {
            pause_command
            if (!message.guild) return;
            const queue = message.client.queue.get(message.guild.id);
            if (!queue) return message.channel.send(
                new MessageEmbed()
                .setTitle("Thare is nothing in the music queue!")
                .setColor("RED")).catch(err => {
                throw new TypeError(err)
            });
            queue.loop = !queue.loop;
            const loopembed = new MessageEmbed()
                .setColor(queue.loop ? "#c219d8" : "#ff0e7a")
                .setAuthor(`Loop is now ${queue.loop ? " enabled" : " disabled"}`)
            message.react("✅");
            return queue.textChannel.send(loopembed).catch(err => {
                throw new TypeError(err)
            });
        } else if (client.help_command == false) {
            message.channel.send(
                new MessageEmbed()
                .setTitle(`\`${module.exports.name}\` Has Been Disabled From The Music System`)
            )
        } else throw new TypeError(`❌ | "${module.exports.name}_command" value must be true or false`)
    }
};