const { MessageEmbed } = require("discord.js");
const lyricsFinder = require("lyrics-finder");

module.exports = {
    name: "lyrics",
    aliases: ["ly", "text"],
    cooldown: 30,
    description: `Gives you the song lyrics!.`,
    run: async(client, message, args) => {
        if (client.lyrics_command == true) {
            if (!message.guild) return;
            message.react("‚úÖ").catch(err => {
                throw new TypeError(err)
            });
            const queue = message.client.queue.get(message.guild.id);
            if (!queue) return message.channel.send(
                new MessageEmbed()
                .setTitle("Thare is nothing in the music queue!")
                .setColor("RED")
            ).catch(err => {
                throw new TypeError(err)
            });
            let lyrics = null;
            let temEmbed = new MessageEmbed()
                .setAuthor("Searching...").setFooter("Lyrics")
                .setColor("#f300e5")
            let result = await message.channel.send(temEmbed)
            try {
                lyrics = await lyricsFinder(queue.songs[0].title, "");
                if (!lyrics) lyrics = `No lyrics found for ${queue.songs[0].title}.`;
            } catch (error) {
                lyrics = `**No lyrics found for ${queue.songs[0].title}.**`;
            }
            let lyricsEmbed = new MessageEmbed()
                .setTitle("üìë Lyrics")
                .setDescription(`**${lyrics}**`)
                .setColor("#f300e5")
            if (lyricsEmbed.description.length >= 2048)
                lyricsEmbed.description = `${lyricsEmbed.description.substr(0, 2045)}...`;
            return result.edit(lyricsEmbed).catch(err => {
                throw new TypeError(err)
            });
        } else if (client.help_command == false) {
            message.channel.send(
                new MessageEmbed()
                .setTitle(`\`${module.exports.name}\` Has Been Disabled From The Music System`)
            )
        } else throw new TypeError(`‚ùå | "${module.exports.name}_command" value must be true or false`)
    }
};