const { MessageEmbed } = require("discord.js");

module.exports = {
    name: "remove",
    aliases: ["delete"],
    cooldown: 5,
    description: `Remove songs from the queue!.`,
    run: async(client, message, args) => {
        if (client.remove_command == true) {
            if (!message.guild) return;
            const queue = message.client.queue.get(message.guild.id);
            if (!queue) return message.channel.send(
                new MessageEmbed()
                .setTitle("Thare is nothing in the music queue!")
                .setColor("RED")).catch(err => {
                throw new TypeError(err)
            });
            if (!args.length) return message.channel.send(
                new MessageEmbed()
                .setTitle(`Try: ${prefix}remove <Music Queue Number>`)
                .setColor("RED")).catch(err => {
                throw new TypeError(err)
            });
            if (isNaN(args[0])) return message.channel.send(
                new MessageEmbed()
                .setTitle(`Try: ${prefix}remove <Music Queue Number>`)
                .setColor("RED")).catch(err => {
                throw new TypeError(err)
            });
            const song = queue.songs.splice(args[0] - 1, 1);
            message.react("✅")
            queue.textChannel.send(new MessageEmbed()
                .setDescription(`❌ | ${message.author} removed **${song[0].title}** from the Queue`)
                .setColor("#c219d8")
            );
        } else if (client.help_command == false) {
            message.channel.send(
                new MessageEmbed()
                .setTitle(`\`${module.exports.name}\` Has Been Disabled From The Music System`)
            )
        } else throw new TypeError(`❌ | "${module.exports.name}_command" value must be true or false`)
    }
};